mapper.select:
	select *
  from dm_maintenance_plan_ 
  where 1=1
    
      and device_id_ = #{deviceId,jdbcType=CHAR}
    
    
      and maintenance_liableman_ = #{maintenanceLiableman,jdbcType=CHAR}
    
    
      and maintenance_status_ = #{maintenanceStatus,jdbcType=CHAR}

	select *
  from dm_maintenance_plan_
  where 1=1
    
      and device_id_ = #{deviceId,jdbcType=CHAR}
    
    
